sys_variable_value.value:
(function execute(inputs, outputs) {

  var gr = new GlideRecord('sc_req_item');
  gr.addQuery('sys_id', inputs.ritm);
  gr.query();
  if(gr.next()){
    var name = inputs.list_name;
    //var list = gr.variables.name;
    var list = gr.variables[name];
    if(inputs.remove_or_add == 'add'){
      if(list == ''){
      	list = list + inputs.variable;
      }
      else{
       list = list +',' + inputs.variable;
      }
    }
    else{//remove
    	//if it can find it in the string
      	//if(list.search(inputs.variable) != -1){
          var arr = (list).toString().split(',');
          for(var i = 0; i < arr.length; i++){
            if(arr[i] == inputs.variable){
           		arr.splice(i,1);
            }
          }
          //refill a new list
          var list_new = '';
          for(var h =0; h < arr.length; h++){
          	list_new += arr[h];  
            if(h < arr.length-1){
             	list_new += ','; 
            }
          }
          list = list_new;
        //}
    }

    //update record with the new list
    outputs.list_out = list;
    //gr.variables.name = list;
    gr.variables[name] = list;
    gr.update();

  }




// ... code ...
})(inputs, outputs);

